import React from 'react';
import { View, Text, Image, TouchableOpacity, ScrollView } from 'react-native';

const beverages = [
  {
    name: 'Café Latte',
    category: 'Cafés',
    description: 'Espresso con leche vaporizada y una pequeña cantidad de espuma de leche.',
    price: '$4.99',
    image: require('../assets/latte.jpg')
  },
  // Agrega más bebidas según sea necesario
];

export default function DrinkMenu() {
  return (
    <ScrollView>
      {beverages.map((drink, index) => (
        <TouchableOpacity key={index}>
          <View style={{ padding: 16, borderBottomWidth: 1, borderColor: '#ccc' }}>
            <Image source={drink.image} style={{ width: 100, height: 100, borderRadius: 10, marginRight: 10 }} />
            <View>
              <Text style={{ fontSize: 18, fontWeight: 'bold' }}>{drink.name}</Text>
              <Text>{drink.category}</Text>
              <Text>{drink.description}</Text>
              <Text>{drink.price}</Text>
            </View>
          </View>
        </TouchableOpacity>
      ))}
    </ScrollView>
  );
}
