import React, { useState } from 'react';
import { View, Text, TouchableOpacity, StyleSheet, FlatList } from 'react-native';

export default function Order({ route }) {
  const { selectedProducts } = route.params || [];
  const [selectedProductIds, setSelectedProductIds] = useState([]);

  const handleToggleProduct = (productId) => {
    const updatedProductIds = selectedProductIds.includes(productId)
      ? selectedProductIds.filter(id => id !== productId)
      : [...selectedProductIds, productId];
    setSelectedProductIds(updatedProductIds);
  }

  const getTotalPrice = () => {
    return selectedProductIds.reduce((total, id) => {
      const product = selectedProducts.find(item => item.id === id);
      return total + (product ? parseFloat(product.price) : 0);
    }, 0).toFixed(2);
  }

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Realizar Pedido</Text>
      <Text style={styles.description}>Selecciona tus productos y procede al pago.</Text>
      <FlatList
        data={selectedProducts}
        keyExtractor={item => item.name}
        renderItem={({ item }) => (
          <TouchableOpacity 
            style={[
              styles.productItem, 
              selectedProductIds.includes(item.id) && styles.selectedProduct
            ]}
            onPress={() => handleToggleProduct(item.id)}
          >
            <Text style={styles.productName}>{item.name}</Text>
            <Text style={styles.productPrice}>S/{parseFloat(item.price).toFixed(2)}</Text>
          </TouchableOpacity>
        )}
      />
      <View style={styles.totalContainer}>
        <Text style={styles.totalText}>Total:</Text>
        <Text style={styles.totalAmount}>S/{getTotalPrice()}</Text>
      </View>
      <TouchableOpacity style={styles.orderButton}>
        <Text style={styles.orderButtonText}>Hacer Pedido</Text>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F7F7F7',
    padding: 16,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 8,
    color: '#333',
  },
  description: {
    fontSize: 16,
    marginBottom: 16,
    color: '#555',
  },
  productItem: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 12,
    backgroundColor: '#FFFFFF',
    borderRadius: 10,
    padding: 10,
  },
  selectedProduct: {
    backgroundColor: '#a8b7ba',
  },
  productName: {
    fontSize: 16,
    color: '#333',
  },
  productPrice: {
    fontSize: 16,
    fontWeight: 'bold',
    color: '#333',
  },
  totalContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginVertical: 10,
  },
  totalText: {
    fontSize: 18,
    fontWeight: 'bold',
    color: '#333',
  },
  totalAmount: {
    fontSize: 18,
    fontWeight: 'bold',
    color: '#333',
  },
  orderButton: {
    backgroundColor: '#FF6F61',
    paddingVertical: 12,
    borderRadius: 10,
    alignItems: 'center',
    marginTop: 20,
  },
  orderButtonText: {
    color: '#FFFFFF',
    fontSize: 18,
    fontWeight: 'bold',
  }
});
