import React from 'react';
import { View, Text, StyleSheet, Image, ScrollView } from 'react-native';

const storeLocations = [
  {
    name: 'Starbucks Real Plaza',
    address: 'Av. Ejercito  123, Ciudad',
    distance: '1.2 km',
    image: require('../assets/store1.jpg')
  },
  {
    name: 'Starbucks Paseo de la Reforma',
    address: 'Paseo de la Reforma 456, Ciudad',
    distance: '2.5 km',
    image: require('../assets/store2.jpg')
  },
  // Agrega más ubicaciones según sea necesario
];

export default function NearbyStores() {
  return (
    <ScrollView style={styles.container}>
      <Text style={styles.title}>Cafeterías Cercanas</Text>
      <Text style={styles.description}>Encuentra las tiendas de Starbucks cercanas a tu ubicación.</Text>
      {storeLocations.map((store, index) => (
        <View key={index} style={styles.storeContainer}>
          <Image source={store.image} style={styles.storeImage} />
          <View style={styles.storeInfo}>
            <Text style={styles.storeName}>{store.name}</Text>
            <Text style={styles.storeAddress}>{store.address}</Text>
            <Text style={styles.storeDistance}>{store.distance}</Text>
          </View>
        </View>
      ))}
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: {
    padding: 16
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 8
  },
  description: {
    fontSize: 16,
    marginBottom: 16,
    color: '#555'
  },
  storeContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 16
  },
  storeImage: {
    width: 100,
    height: 100,
    borderRadius: 10,
    marginRight: 16
  },
  storeInfo: {
    flex: 1
  },
  storeName: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 4
  },
  storeAddress: {
    fontSize: 14,
    marginBottom: 4,
    color: '#555'
  },
  storeDistance: {
    fontSize: 14,
    color: '#555'
  }
});
