import React, {useState} from 'react';
import { Button } from 'react-native';
import { Text, View } from 'react-native';

const Gato = (props) => {
  const [conHambre, indicarTieneHambre] = useState(true);

  return (
    <View>
      <Text>
        Soy {props.name}, y estoy {conHambre ? 'con hambre' : 'lleno'}!
      </Text>
      <Button
        onPress={() => {
          indicarTieneHambre(false);
        }}
        disabled={!conHambre}
        title={conHambre ? 'Dame comida, por favor' : 'Gracias'}
      />
    </View>
  );
}

const Felino = () => {
  return (
   <View>
      <Text>Presentacion!</Text>
      <Gato name="Michi" />
      <Gato name="Michito" />
      <Gato name="Michifuz" />
   </View>
  )
}

export default Felino;
