import React, { useState } from 'react';
import { View, Text, Switch, TouchableOpacity, StyleSheet } from 'react-native';
import Modal from 'react-native-modal';

export default function Settings() {
  const [receiveNotifications, setReceiveNotifications] = useState(true);
  const [isModalVisible, setModalVisible] = useState(false);

  const toggleModal = () => {
    setModalVisible(!isModalVisible);
  };

  const handleSignOut = () => {
    toggleModal();
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Configuraciones</Text>
      <View style={styles.option}>
        <Text style={styles.optionText}>Recibir Notificaciones</Text>
        <Switch
          value={receiveNotifications}
          onValueChange={(value) => setReceiveNotifications(value)}
        />
      </View>
      {/* ... Otras opciones de configuración */}
      <TouchableOpacity style={styles.option} onPress={handleSignOut}>
        <Text style={styles.optionText}>Cerrar Sesión</Text>
      </TouchableOpacity>

      <Modal isVisible={isModalVisible}>
        <View style={styles.modalContent}>
          <Text style={styles.modalText}>¿Estás seguro?</Text>
          <TouchableOpacity onPress={handleSignOut} style={styles.modalButton}>
            <Text style={styles.modalButtonText}>Cerrar Sesión</Text>
          </TouchableOpacity>
          <TouchableOpacity onPress={toggleModal} style={styles.modalButton}>
            <Text style={styles.modalButtonText}>Cancelar</Text>
          </TouchableOpacity>
        </View>
      </Modal>
    </View>
  );
}

const styles = StyleSheet.create({
  // ... Estilos anteriores

  modalContent: {
    backgroundColor: 'white',
    padding: 22,
    justifyContent: 'center',
    alignItems: 'center',
    borderRadius: 4,
    borderColor: 'rgba(0, 0, 0, 0.1)',
  },
  modalText: {
    marginBottom: 15,
    textAlign: 'center',
  },
  modalButton: {
    padding: 10,
    marginTop: 10,
    backgroundColor: '#00704A',
    borderRadius: 5,
  },
  modalButtonText: {
    color: 'white',
    textAlign: 'center',
    fontWeight: 'bold',
  },
});
