import React, { useState, useEffect } from 'react';
import { View, Text, Image, TouchableOpacity, ScrollView, StyleSheet } from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useNavigation } from '@react-navigation/native';

const Menu = () => {
  const [beverages, setBeverages] = useState([]);
  const [selectedProducts, setSelectedProducts] = useState([]);
  const navigation = useNavigation();

  useEffect(() => {
    fetch('http://localhost:5000/api/bebidas')
      .then(response => response.json())
      .then(data => {
        setBeverages(data);
      })
      .catch(error => {
        console.error('Error al obtener las bebidas:', error);
      });
  }, []);

  const handleToggleProduct = (product) => {
    const updatedProducts = selectedProducts.includes(product)
      ? selectedProducts.filter(item => item !== product)
      : [...selectedProducts, product];
    setSelectedProducts(updatedProducts);
  }

  return (
    <ScrollView contentContainerStyle={{ padding: 15 }}>
      {beverages.map((drink, index) => (
        <TouchableOpacity key={index}>
          <View style={styles.container}>
            <Image source={{ uri: drink.image }} style={styles.image} />
            <View style={styles.details}>
              <Text style={styles.name}>{drink.name}</Text>
              <Text style={styles.category}>{drink.category}</Text>
              <Text style={styles.description}>{drink.description}</Text>
              <Text style={styles.price}>{drink.price}</Text>
              <TouchableOpacity
                onPress={() => handleToggleProduct(drink)}
                style={[styles.addToCartButton, selectedProducts.includes(drink) && styles.selectedProduct]}
              >
                <Text style={styles.addToCartText}>AÃ±adir al carrito</Text>
              </TouchableOpacity>
            </View>
          </View>
        </TouchableOpacity>
      ))}
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: '#fff',
    borderRadius: 12,
    marginVertical: 10,
    padding: 15,
    flexDirection: 'row',
    alignItems: 'center',
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
  },
  image: {
    width: 100,
    height: 100,
    borderRadius: 10,
    marginRight: 15,
  },
  details: {
    flex: 1,
  },
  name: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 5,
  },
  category: {
    color: '#555',
    marginBottom: 5,
  },
  description: {
    marginBottom: 10,
    color: '#666',
    fontSize: 14,
  },
  price: {
    fontSize: 16,
    fontWeight: 'bold',
    color: '#333',
  },
  addToCartButton: {
    backgroundColor: '#00704A',
    paddingVertical: 10,
    paddingHorizontal: 20,
    borderRadius: 25,
    marginTop: 10,
  },
  addToCartText: {
    color: '#fff',
    fontSize: 14,
    fontWeight: 'bold',
  },
  selectedProduct: {
    backgroundColor: '#a8b7ba',
  },
});

export default Menu;
