import React, { useState, useEffect } from 'react';
import { View, Text, Image, TouchableOpacity, ScrollView, StyleSheet } from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useNavigation } from '@react-navigation/native';

export default function Menu() {
  const [beverages, setBeverages] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null); // Cambiado a un solo producto seleccionado
  const navigation = useNavigation();

  useEffect(() => {
    navigation.setOptions({
      headerLeft: () => (
        <TouchableOpacity onPress={() => navigation.goBack()} style={{ marginLeft: 15 }}>
          <Ionicons name="arrow-back" size={24} color="#00704A" />
        </TouchableOpacity>
      ),
      headerRight: () => (
        <TouchableOpacity
          onPress={() => navigation.navigate('Pedido', { selectedProducts: [selectedProduct] })}
          style={{ marginRight: 15 }}
        >
          <Ionicons
            name="cart"
            size={24}
            color="#00704A"
          />
          {selectedProduct && (
            <View style={styles.cartCountContainer}>
              <Text style={styles.cartCount}>1</Text>
            </View>
          )}
        </TouchableOpacity>
      ),
    });

    // Obtener las bebidas desde la API
    fetch('http://localhost:5000/api/bebidas')
      .then(response => response.json())
      .then(data => setBeverages(data))
      .catch(error => console.error('Error al obtener las bebidas:', error));
  }, [navigation, selectedProduct]);

  const handleToggleProduct = (product) => {
    setSelectedProduct(prevProduct => (prevProduct === product ? null : product));
  }

  return (
    <ScrollView contentContainerStyle={{ padding: 15 }}>
      {beverages.map((drink, index) => (
        <TouchableOpacity key={index} onPress={() => handleToggleProduct(drink)}>
          <View style={styles.container}>
            <Image source={{ uri: `http://localhost:5000${drink.image}` }} style={styles.image} />
            <View style={styles.details}>
              <Text style={styles.name}>{drink.name}</Text>
              <Text style={styles.category}>{drink.category}</Text>
              <Text style={styles.description}>{drink.description}</Text>
              <Text style={styles.price}>{drink.price}</Text>
              <TouchableOpacity
                onPress={() => handleToggleProduct(drink)}
                style={[
                  styles.addToCartButton,
                  selectedProduct === drink && styles.selectedProduct
                ]}
                disabled={selectedProduct !== null && selectedProduct !== drink}
              >
                <Text style={styles.addToCartText}>
                  {selectedProduct === drink ? 'Eliminar del carrito' : 'AÃ±adir al carrito'}
                </Text>
              </TouchableOpacity>
            </View>
          </View>
        </TouchableOpacity>
      ))}
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: '#fff',
    borderRadius: 12,
    marginVertical: 10,
    padding: 15,
    flexDirection: 'row',
    alignItems: 'center',
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
  },
  image: {
    width: 100,
    height: 100,
    borderRadius: 10,
    marginRight: 15,
  },
  details: {
    flex: 1,
  },
  name: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 5,
  },
  category: {
    color: '#555',
    marginBottom: 5,
  },
  description: {
    marginBottom: 10,
    color: '#666',
    fontSize: 14,
  },
  price: {
    fontSize: 16,
    fontWeight: 'bold',
    color: '#333',
  },
  cartCountContainer: {
    position: 'absolute',
    top: 0,
    right: 0,
    backgroundColor: '#FF6F61',
    borderRadius: 10,
    paddingHorizontal: 6,
    paddingVertical: 2,
  },
  cartCount: {
    color: '#FFFFFF',
    fontSize: 12,
    fontWeight: 'bold',
  },
  addToCartButton: {
    backgroundColor: '#00704A',
    paddingVertical: 10,
    paddingHorizontal: 20,
    borderRadius: 25,
    marginTop: 10,
  },
  addToCartText: {
    color: '#fff',
    fontSize: 14,
    fontWeight: 'bold',
  },
  selectedProduct: {
    backgroundColor: '#a8b7ba',
  },
});
